import React, { useState } from 'react';
// import Card from '../UI/Card'
import ExpenseFilter from "./ExpenseFilter.js";
import ExpenseItem from "./ExpenseItem";
// import './ExpenseFilter.js'
import './Expenses.css';

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState('2023 ');

  const filterChangeHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
  };

  const fileteredExpenses = props.items.filter(expense=>{
    return expense.date.getFullYear().toString()===filteredYear;
  });
  
  const NoEpenseContent=<p>No expenses found!</p>;

  if(fileteredExpenses.length>0)
  {
    NoEpenseContent
  }

  return (
    <div className='expenses'>
      <ExpenseFilter 
        selected={filteredYear} 
        onChangeFilter={filterChangeHandler}   
      />
      {fileteredExpenses.length===0 && NoEpenseContent}
      {fileteredExpenses.length>0 &&
        fileteredExpenses.map((expense) => (
        <ExpenseItem 
          key={expense.id}
          title={expense.title}
          amount={expense.amount} 
          date={expense.date} 
          />
  ))}
    </div>
  );
}

export default Expenses;
